$(document).ready(function(){$("table td a.btn.btn-danger.btn-xs").click(function(){var a=$(this).attr("name");var b=$(this).parent().parent().attr("class");$.ajax({type:"DELETE",url:a,async:true,cache:false,success:function(c){var d=c;$("#text").text(d);$("#status").fadeIn("slow",function(){$("#status").css("display","block");$("#status").delay(1500);$("#status").fadeOut("slow",function(){$("#status").slideUp(1500)})});$("."+b).fadeOut("slow",function(){$("."+b).remove()})},statusCode:{400:function(c){var d=c.responseText;$("#text-error").text(d);$("#error").fadeIn("slow",function(){$("#error").css("display","block")})}}})});$("a.btn.btn-danger.btn-sm").click(function(){var a=$(this).attr("name");var c=a.substring(a.indexOf("[")+1,a.indexOf("]"));var c=c.replace(/"/g,"");var b=c.split(",");$.ajax({type:"DELETE",url:a,async:true,cache:false,success:function(d){var f=d;$("#text").text(f);$("#status").fadeIn("slow",function(){$("#status").css("display","block");$("#status").delay(1500);$("#status").fadeOut("slow",function(){$("#status").slideUp(1500)})});for(var e in b){$(".tr"+b[e]).fadeOut("slow",function(){$(".tr"+b[e]).remove()})}},statusCode:{400:function(d){var e=d.responseText;$("#text-error").text(e);$("#error").fadeIn("slow",function(){$("#error").css("display","block")})}}})})});